# -*- coding: utf-8 -*-# 对request请求进行封装import requests, jsondef send_get(url, headers):  # get消息    r = requests.get(url, headers=headers)    r.encoding = 'UTF-8'    json_response = json.loads(r.text)    return r.status_code, json_responsedef send_post(url, data, headers):  # post消息    r = requests.post(url, data=json.dumps(data), headers=headers)    r.encoding = 'UTF-8'    json_response = json.loads(r.text)    return r.status_code, json_responsedef send_requests(url, method, data, headers):    ret = None    if method.lower() == 'get':        ret = send_get(url, headers=headers)    elif method.lower() == 'post':        ret = send_post(url, data, headers=headers)    return ret